import React, { useState } from 'react';

const SearchBar = ({ onSearch }) => {
  const [purchaseId, setPurchaseId] = useState('');

  const handleSearch = () => {
    // Ensure only numeric characters are included in the search
    const sanitizedPurchaseId = purchaseId.replace(/[^0-9]/g, '');
    onSearch(sanitizedPurchaseId);
  };

  const handleChange = (e) => {
    // Ensure only numeric characters are allowed in the input
    const sanitizedValue = e.target.value.replace(/[^0-9]/g, '');
    setPurchaseId(sanitizedValue);
  };

  return (
    <div>
      <label htmlFor="purchaseId">Enter brand/model: </label>
      <input
        type="text"
        id="purchaseId"
        value={purchaseId}
        onChange={handleChange}
        placeholder="Enter Purchase ID"
      />
      <button onClick={handleSearch}>Search</button>
    </div>
  );
};

export default SearchBar;
