// SearchBar.js
import React, { useState, useEffect } from 'react';
import Select from 'react-select';

const SearchBar = ({ onSearch }) => {
  const [purchaseId, setPurchaseId] = useState('');
  const [matchedRecords, setMatchedRecords] = useState([]);
  const [selectedOption, setSelectedOption] = useState(null);

  const handleSearch = () => {
    onSearch(selectedOption.value);
  };

  const handleInputChange = (inputValue) => {
    setPurchaseId(inputValue);
  };

  useEffect(() => {
    // Fetch matched records based on the input value
    const fetchMatchedRecords = async () => {
      try {
        const response = await fetch(`http://localhost:5466/api/cars?search=${purchaseId}`);
        const data = await response.json();
        setMatchedRecords(data.map((record) => ({ value: record.purchase_id, label: record.purchase_id })));
      } catch (error) {
        console.error('Error fetching matched records:', error);
      }
    };

    // Fetch only if the input value is not empty
    if (purchaseId.trim() !== '') {
      fetchMatchedRecords();
    } else {
      setMatchedRecords([]); // Clear matched records when the input is empty
    }
  }, [purchaseId]);

  return (
    <div>
      <label htmlFor="purchaseId">Enter Purchase ID:</label>
      <Select
        id="purchaseId"
        value={selectedOption}
        onChange={(option) => {
          setSelectedOption(option);
          handleSearch(); // Trigger search on option change
        }}
        onInputChange={handleInputChange}
        options={matchedRecords}
      />
    </div>
  );
};

export default SearchBar;
