import React, { useState } from 'react';

const SearchBar = ({ onSearch }) => {
    const [purchaseId, setPurchaseId] = useState('');
    const [matchedRecords, setMatchedRecords] = useState([]);

  const handleSearch = () => {
    onSearch(purchaseId);
  };

  const handleInputChange = (e) => {
    const value = e.target.value;
    setPurchaseId(value);
  };

  useEffect(() => {
    // Fetch matched records based on the input value
    const fetchMatchedRecords = async () => {
      try {
        const response = await fetch(`http://localhost:5466/api/cars?search=${purchaseId}`);
        const data = await response.json();
        setMatchedRecords(data);
      } catch (error) {
        console.error('Error fetching matched records:', error);
      }
    };

    // Fetch only if the input value is not empty
    if (purchaseId.trim() !== '') {
      fetchMatchedRecords();
    } else {
      setMatchedRecords([]); // Clear matched records when the input is empty
    }
  }, [purchaseId]);

  return (
    <div>
      <label htmlFor="purchaseId">Enter Purchase ID:</label>
      <input
        type="text"
        id="purchaseId"
        value={purchaseId}
        onChange={handleInputChange}
        placeholder="Enter an identification number"
      />
      <button onClick={handleSearch}>Search</button>

      {matchedRecords.length > 0 && (
        <div>
          <h4>Matched Records:</h4>
          <ul>
            {matchedRecords.map((record) => (
              <li key={record.purchase_id}>{record.purchase_id}</li>
              // You can customize the display of matched records as needed
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchBar;
