import React, { useState } from 'react';
import SearchBar from './Searchbar';

const App = () => {
  const [carInfo, setCarInfo] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async (purchaseId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/car/${purchaseId}`);
      if (!response.ok) {
        throw new Error('Purchase ID not found');
      }
      const data = await response.json();
      setCarInfo(data);
      setError(null); // Clear any previous error
    } catch (error) {
      console.error('Error fetching car information:', error.message);
      setCarInfo(null); // Clear carInfo on error
      setError('Purchase ID not found');
    }
  };

  const renderTable = () => {
    if (!carInfo || carInfo.length === 0) {
      return null;
    }

    const headers = Object.keys(carInfo[0]);

    return (
      <table>
        <thead>
          <tr>
            {headers.map((header) => (
              <th key={header}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {carInfo.map((row, index) => (
            <tr key={index}>
              {headers.map((header) => (
                <td key={header}>{row[header]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div>
      <h1>Car Dealership Finance Department</h1>
      <SearchBar onSearch={handleSearch} />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {renderTable()}
    </div>
  );
};

export default App;
